package transaction

import (
	"testing"
	"reflect"
)

func TestTrytesToObject(t *testing.T) {
	trytes := "";
	result := TrytesToObject(trytes)
	if !reflect.DeepEqual(result.Address, "OFFLINE9SPAM9ADDRESS99999999999999999999999999999999999999999999999999999999TYPPI") {
		t.Error("Wrong address!")
	}
	if !reflect.DeepEqual(result.Tag, "IOTASPAM9DOT9COM9999TYPPI99") {
		t.Error("Wrong tag!")
	}
	if !reflect.DeepEqual(result.Bundle, "BFISVJDKLL9XYGQNPQZZCWCJISYRJZGYAJNDVVJQYEPHUYI9VOFVKAXSAWUD9JFALDKIQJGHPQKRDD99A") {
		t.Error("Wrong bundle!")
	}
	if !reflect.DeepEqual(result.Value, int64(0)) {
		t.Error("Wrong value!")
	}
	if !reflect.DeepEqual(result.Nonce, "CAB9999999IWB99999999999999") {
		t.Error("Wrong nonce!")
	}
}
